let now = new Date(),
lct = window.location.search;

now.setMonth(now.getMonth() + 6)
now = now.toUTCString();
/** querySelector(All)を短縮したものです
 * @param {string} x 
 * @returns {Element | NodeListOf<Element>}
 */
let $ = x => x.includes("#")? // xに"#"が含まれていた場合
           document.querySelector(x): // ...querySelector(x)
           document.querySelectorAll(x); // 偽の場合,              //
                                         // ...querySelectorAll(x) //
/** cookieを追加/編集することができます 
 * @param {string} key Cookieの名前です
 * @param {string} data Cookieに入れておくデータです
 * @param {string | number | undefined} day 【任意】Cookieの保存期間です
 * @returns {void}
 */
let SetCookie = (key, data, day) => {
    day?
        (typeof day) === "number"?
            document.cookie = `${key}=${data}; max-age=${day}; SameSite=Lax;`:
            document.cookie = `${key}=${data}; expires=${day}; SameSite=Lax;`
    :document.cookie = `${key}=${data}; SameSite=Lax;`;
};
/** cookieを取得することができます
 * @param {string} key Cookieの名前です
 * @returns {string | number}
 */
let GetCookie = key => {
    /**@type {RegExpMatchArray?} */
    let cache = eval(`document.cookie.match(/(^| )${key}=([^;]+)/)`);
    return cache?
    cache[2].toString(): 0;

};
